        private string GetVendor(string strMAC)
        {
            if (strMAC.Length < 6)
                return "Unknown";

            string StartMAC = strMAC.Substring(0, 6);
            if (lstMACVendors.ContainsKey(StartMAC))
                return lstMACVendors[StartMAC];

            return "Unknown";
        }

        void tmr_Tick(object sender, EventArgs e)
        {
            this.Hide();

            GetGatewaysIP();

            Dictionary<string, string> ArpTable = GetArpTable();

            if (ArpTable != null)
            {
                foreach (string ip in Addresses.Keys.ToList())
                {
                    if (ArpTable.ContainsKey(ip))
                    {
                        string strNewMAC = ArpTable[ip];
                        if (Addresses[ip] != "" && Addresses[ip] != strNewMAC)
                        {
                            notifyIcon1.ShowBalloonTip(3600000, "ARP Poisoning Detected", 
                                string.Format("IP: {0} New MAC: {1} ({2})", ip, strNewMAC, GetVendor(strNewMAC)), 
                                ToolTipIcon.Error);
                            this.bIsInARPP = true;
                            SetIcon(bIsInARPP);
                        }

                        Addresses[ip] = strNewMAC;
                    }
                }
            }
        }

        private void GetGatewaysIP()
        {
            NetworkInterface[] interfaces = NetworkInterface.GetAllNetworkInterfaces();

            foreach (NetworkInterface card in interfaces)
            {
                if (card != null)
                {
                    var address = card.GetIPProperties().GatewayAddresses.FirstOrDefault();
                    if (address != null)
                    {
                        string strAddr = address.Address.ToString();
                        if (!Addresses.ContainsKey(strAddr))
                            Addresses.Add(strAddr, "");
                    }
                }
            }
        }

        #region get MAC

        [DllImport("IpHlpApi.dll")]
        [return: MarshalAs(UnmanagedType.U4)]
        static extern int GetIpNetTable(IntPtr pIpNetTable,
              [MarshalAs(UnmanagedType.U4)] ref int pdwSize, bool bOrder);

        private Dictionary<string, string> GetArpTable()
        {
            Dictionary<string, string> all = new Dictionary<string, string>();

            int spaceForNetTable = 0;
            GetIpNetTable(IntPtr.Zero, ref spaceForNetTable, false);
            IntPtr rawTable = IntPtr.Zero;
            try
            {
                rawTable = Marshal.AllocCoTaskMem(spaceForNetTable);

                int errorCode = GetIpNetTable(rawTable, ref spaceForNetTable, false);
                if (errorCode != 0)
                    return null;

                int rowsCount = Marshal.ReadInt32(rawTable);
                IntPtr currentBuffer = new IntPtr(rawTable.ToInt64() + Marshal.SizeOf(typeof(Int32)));

                MIB_IPNETROW[] rows = new MIB_IPNETROW[rowsCount];
                for (int index = 0; index < rowsCount; index++)
                {
                    rows[index] = (MIB_IPNETROW)Marshal.PtrToStructure(new IntPtr(currentBuffer.ToInt64() +
                                                (index * Marshal.SizeOf(typeof(MIB_IPNETROW)))
                                               ),
                                                typeof(MIB_IPNETROW));
                }

                PhysicalAddress virtualMAC = new PhysicalAddress(new byte[] { 0, 0, 0, 0, 0, 0 });
                PhysicalAddress broadcastMAC = new PhysicalAddress(new byte[] { 255, 255, 255, 255, 255, 255 });
                foreach (MIB_IPNETROW row in rows)
                {
                    IPAddress ip = new IPAddress(BitConverter.GetBytes(row.dwAddr));
                    byte[] rawMAC = new byte[] { row.mac0, row.mac1, row.mac2, row.mac3, row.mac4, row.mac5 };
                    PhysicalAddress pa = new PhysicalAddress(rawMAC);
                    if (!pa.Equals(virtualMAC) && !pa.Equals(broadcastMAC) && !IsMulticast(ip))
                    {
                        if (!all.ContainsKey(ip.ToString()))
                        {
                            all.Add(ip.ToString(), pa.ToString());
                        }
                    }
                }
            }
            finally
            {
                Marshal.FreeCoTaskMem(rawTable);
            }
            return all;
        }

        [StructLayout(LayoutKind.Sequential)]
        struct MIB_IPNETROW
        {
            [MarshalAs(UnmanagedType.U4)]
            public int dwIndex;
            [MarshalAs(UnmanagedType.U4)]
            public int dwPhysAddrLen;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac0;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac1;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac2;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac3;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac4;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac5;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac6;
            [MarshalAs(UnmanagedType.U1)]
            public byte mac7;
            [MarshalAs(UnmanagedType.U4)]
            public int dwAddr;
            [MarshalAs(UnmanagedType.U4)]
            public int dwType;
        }

        private bool IsMulticast(IPAddress ip)
        {
            bool result = true;
            if (!ip.IsIPv6Multicast)
            {
                byte highIP = ip.GetAddressBytes()[0];
                if (highIP < 224 || highIP > 239)
                {
                    result = false;
                }
            }
            return result;
        }

        #endregion

        private string GetCurrentGateWays()
        {
            string result = "";
            if (bIsInARPP)
                result = "ARP Poisoning Detected" + Environment.NewLine;

            foreach (string ip in Addresses.Keys)
            {
                if (!ip.Contains(":"))
                    result += string.Format("{0} : {1} ({2})\r\n", ip, Addresses[ip], GetVendor(Addresses[ip]));
                    //result += ip + " : " + Addresses[ip] + Environment.NewLine;
            }

            if (result.Length > 2)
                result.Remove(result.Length - 2);

            if (result == "") result = "None";
            return result;
        }

